/**
 * Utility: parse dd/mm/yyyy into a UTC‐normalized Date, or null.
 */
function parseDate(dateStr) {
  if (!dateStr || dateStr.toLowerCase() === "null") return null;
  const [day, month, year] = dateStr.split('/');
  return new Date(Date.UTC(+year, +month - 1, +day));
}

/**
 * Utility: days between given UTC‐normalized date and today (UTC).
 */
function daysSince(dateObj) {
  if (!dateObj) return null;
  const now = new Date();
  const utcNow = Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate());
  const utcThen = Date.UTC(dateObj.getUTCFullYear(), dateObj.getUTCMonth(), dateObj.getUTCDate());
  return Math.floor((utcNow - utcThen) / (1000 * 60 * 60 * 24));
}

for (const item of $input.all()) {
  const doc = JSON.parse(JSON.stringify(item.json || {}));
  const name = doc.Name || "Unknown";

  // --- parse all relevant dates ---
  const connectionDate    = parseDate(doc['Connection Date']);
  const firstLIDate       = parseDate(doc.First_linkedin_msg_sent_date);
  const firstEmailDate    = parseDate(doc.First_email_sent_date);
  const secondLIDate      = parseDate(doc.Second_linkedin_msg_sent_date);
  const secondEmailDate   = parseDate(doc.Second_email_sent_date);
  const thirdLIDate       = parseDate(doc.Third_linkedin_msg_sent_date);
  const callDate          = parseDate(doc.Call_date);  // if you track when the call was logged

  // --- has each action already been sent? ---
  const firstLISent     = doc.First_linkedin_msg_sent === "Yes";
  const firstEmailSent  = doc.First_email_sent === "Yes";
  const secondLISent    = doc.Second_linkedin_msg_sent === "Yes";
  const secondEmailSent = doc.Second_email_sent === "Yes";
  const thirdLISent     = doc.Third_linkedin_msg_sent === "Yes";
  const callLogged      = doc.Call_made === "Yes";

  // --- compute days-since for each date ---
  const daysSinceConnection    = daysSince(connectionDate);
  const daysSinceFirstLI       = daysSince(firstLIDate);
  const daysSinceFirstEmail    = daysSince(firstEmailDate);
  const daysSinceSecondLI      = daysSince(secondLIDate);
  const daysSinceSecondEmail   = daysSince(secondEmailDate);
  const daysSinceThirdLI       = daysSince(thirdLIDate);

  // --- build decision flags & action list ---
  const actions = [];

  // 1) First LinkedIn @ 5 days post-connection
  const flagFirstLI = !firstLISent && daysSinceConnection === 5;
  if (flagFirstLI) actions.push("Send First LinkedIn Message (5 days)");

  // 2) First Email @ 1 day after First LinkedIn
  const flagFirstEmail = firstLISent && !firstEmailSent && daysSinceFirstLI === 1;
  if (flagFirstEmail) actions.push("Send First Email (1 day)");

  // 3) Second LinkedIn @ 2 days after First Email
  const flagSecondLI = firstEmailSent && !secondLISent && daysSinceFirstEmail === 2;
  if (flagSecondLI) actions.push("Send Second LinkedIn Message (2 days)");

  // 4) Second Email @ 3 days after Second LinkedIn
  const flagSecondEmail = secondLISent && !secondEmailSent && daysSinceSecondLI === 3;
  if (flagSecondEmail) actions.push("Send Second Email (3 days)");

  // 5) Third LinkedIn @ 5 days after Second Email
  const flagThirdLI = secondEmailSent && !thirdLISent && daysSinceSecondEmail === 5;
  if (flagThirdLI) actions.push("Send Third LinkedIn Message (5 days)");

  // 6) Call @ 1 day after Third LinkedIn
  const flagCall = thirdLISent && !callLogged && daysSinceThirdLI === 1;
  if (flagCall) actions.push("Log/Make Call (1 day)");

  // --- write results back onto item.json ---
  doc.dateDiffResults = {
    "Lead Name": name,
    "Days Since Connection": daysSinceConnection,
    "First LI Days": daysSinceFirstLI,
    "First Email Days": daysSinceFirstEmail,
    "Second LI Days": daysSinceSecondLI,
    "Second Email Days": daysSinceSecondEmail,
    "Third LI Days": daysSinceThirdLI,
    // your boolean routing flags:
    "First LinkedIn 5-day":    flagFirstLI,
    "First Email 1-day":       flagFirstEmail,
    "Second LinkedIn 2-day":   flagSecondLI,
    "Second Email 3-day":      flagSecondEmail,
    "Third LinkedIn 5-day":    flagThirdLI,
    "Call 1-day":              flagCall
  };

  doc.dateSummary = {
    name,
    connection_days:   daysSinceConnection,
    firstLI_5days:     flagFirstLI,
    firstEmail_1day:   flagFirstEmail,
    secondLI_2days:    flagSecondLI,
    secondEmail_3days: flagSecondEmail,
    thirdLI_5days:     flagThirdLI,
    call_1day:         flagCall
  };

  doc.actionsNeeded = actions;
  doc.needsAttention = actions.length > 0;

  // push back into the workflow
  item.json = doc;
}

return $input.all();
